mapper.update:
update article


    set articlename=#{articlename},
    text=#{text}


    set articlename=#{articlename},
    text=#{text},
    Image=#{Image}

id=#{id}

mapper.select:
select a.*,b.img
from article  a  join  user  b on a.authorid=b.id

    a.articlename like "%" #{articlename} "%";

select a.*,b.img
from article  a  join  user  b on a.authorid=b.id
a.author=#{author}
